package com.tools;

public class Student {
	public int id;
	public double score;
	public String name;
	public String grade;

	public Student() {
		// TODO Auto-generated constructor stub
	}

	public Student(int id, double score, String name, String grade) {
		this.id = id;
		this.score = score;
		this.name = name;
		this.grade = grade;
	}
}
